let finalTemplateDir = ''
try {
  finalTemplateDir = await createNewTemplate(resolveDir(model), resolveDir(templateDir))
  this.log(printInstructionsForNewTemplate(finalTemplateDir))
} catch (error) {
  this.log(dingKats.ERROR + ` cannot create template: ${error}`)
  return
}

const code = finalTemplateDir + suffixes.SAMPLE_DIR
const newModel = finalTemplateDir + suffixes.MODEL_DIR

try {
  await removeCodeModelDiscrepancies(
    finalTemplateDir, code, newModel
  )
} catch (error) {
  this.log(dingKats.ERROR + ` cannot compare directories: ${error}`)
}

const codeDir = resolveDir(code)

try {
  const config = await getConfig(codeDir +
    `/${dirNames.META}/${dirNames.TEMPLATE}`)
  const nsInfo = await getNsInfo(codeDir)
  const originalSettings = JSON.parse(JSON.stringify(nsInfo))

  const updatedSettings = await settingsMenu(
    config, nsInfo, codeDir
  )
  if (!updatedSettings) return

  const changedSettings = diff(originalSettings, updatedSettings)
  if (Object.keys(changedSettings).length > 0) {
    // there were changes to the settings made
    await promptToGenerateCode(codeDir)
  }
} catch (error) {
  this.error(error)
  throw new Error(`error with settings: ${error}`)
}

